	#Weapon_point.x,
	200,  ' Ene
	20,   ' Len
	100,  ' Speed
	40,   ' W
	100,  ' Rota
        0,    ' TexNo
        0,    'Hitで消える？
        0,
        0);
ENDFUNCTION;

